package sravya1;

public class Switch {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char grade='D';
		switch (grade)
		{
		case 'A':
		System.out.println("Excellent");
		break;
		case 'B':
	    System.out.println("Marvellous");
	    break;
		case 'D':
	    System.out.println("Fantastic");
	    break;
	    default:
	    System.out.println("Invalid Grade");	
		}

	}

}
